// متغیرهای کنترل برای کتاب‌ها و لیست‌های مختلف
let bookIndex = Math.floor(Math.random() * books.length); // انتخاب کتاب تصادفی
let readBooks = JSON.parse(localStorage.getItem('readBooks')) || []; // لیست کتاب‌های خوانده‌شده
let favorites = JSON.parse(localStorage.getItem('favorites')) || []; // لیست کتاب‌های علاقه‌مندی

// نمایش کتاب پیشنهادی
function showBook() {
    const book = books[bookIndex];
    const descriptionLimit = 200; // طول محدودیت توضیحات
    let description = book.description;
    
    // اگر توضیحات بیش از حد تعیین‌شده باشد، لینک "مطالعه بیشتر" اضافه می‌شود
    if (description.length > descriptionLimit) {
        description = description.substring(0, descriptionLimit) + '... <a href="#" onclick="showFullDescription()">مطالعه بیشتر</a>';
    }
    
    document.getElementById('book').innerHTML = `
    <span class='text-primary'>${book.title}</span>
    <br>
    <span class='text-success'>نویسنده: ${book.author}</span>
    <br>
    ${description}
    `;
}

// نمایش کتاب‌های خوانده‌شده
function displayReadBooks() {
    const readBooksList = document.getElementById('readBooksList');
    readBooksList.innerHTML = ''; // پاک کردن لیست قبلی

    readBooks.forEach((book, index) => {
        const listItem = document.createElement('li');
        listItem.classList.add('list-group-item');
        listItem.innerHTML = `
            <a href="javascript:void(0);" onclick="showBookDescriptionModal('read', ${index})" class="text-decoration-none text-primary">${book.title}</a> - ${book.author}
            <button class="btn btn-danger btn-sm float-start" onclick="confirmRemove('read', ${index})">حذف</button>`;
        readBooksList.appendChild(listItem);
    });

    // نمایش تعداد کتاب‌های خوانده‌شده
    document.getElementById('readBooksCount').textContent = `تعداد  : ${readBooks.length}`;

    localStorage.setItem('readBooks', JSON.stringify(readBooks));
}

// نمایش کتاب‌های علاقه‌مندی
function displayFavorites() {
    const favoritesList = document.getElementById('favoritesList');
    favoritesList.innerHTML = ''; // پاک کردن لیست قبلی

    favorites.forEach((book, index) => {
        const listItem = document.createElement('li');
        listItem.classList.add('list-group-item');
        listItem.innerHTML = `
            <a href="javascript:void(0);" onclick="showBookDescriptionModal('favorite', ${index})" class="text-decoration-none text-primary">${book.title}</a> - ${book.author}
            <button class="btn btn-danger btn-sm float-start" onclick="confirmRemove('favorite', ${index})">حذف</button>`;
        favoritesList.appendChild(listItem);
    });

    // نمایش تعداد کتاب‌های علاقه‌مندی
    document.getElementById('favoritesCount').textContent = `تعداد  : ${favorites.length}`;

    localStorage.setItem('favorites', JSON.stringify(favorites));
}

// تابع برای نمایش توضیحات کامل کتاب
function showFullDescription() {
    const book = books[bookIndex];
    document.getElementById('book').innerHTML = `

    <span class='text-primary'>${book.title}</span>
    <br>
    <span class='text-success'>نویسنده: ${book.author}</span>
    <br>
    <p>${book.description}</p>

    `;
}

// جابجایی به کتاب بعدی
document.getElementById('nextBook').addEventListener('click', () => {
    bookIndex = (bookIndex + 1) % books.length;
    showBook();
});

// جابجایی به کتاب قبلی
document.getElementById('prevBook').addEventListener('click', () => {
    bookIndex = (bookIndex - 1 + books.length) % books.length;
    showBook();
});

// تایید حذف کتاب
function confirmRemove(listType, index) {
    const confirmation = confirm('آیا مطمئن هستید که می‌خواهید این کتاب را حذف کنید؟');
    if (confirmation) {
        removeBook(listType, index);
    }
}

// حذف کتاب از لیست‌های مختلف
function removeBook(listType, index) {
    if (listType === 'read') {
        readBooks.splice(index, 1);
        displayReadBooks();
    } else if (listType === 'favorite') {
        favorites.splice(index, 1);
        displayFavorites();
    }
}

// افزودن کتاب به لیست خوانده‌شده
document.getElementById('readBook').addEventListener('click', () => {
    const book = books[bookIndex];

    // بررسی اینکه آیا کتاب قبلاً در لیست خوانده‌شده موجود است
    if (readBooks.some(b => b.title === book.title)) {
        alert('این کتاب قبلاً به لیست کتاب‌های خوانده‌شده اضافه شده است.');
        return;
    }

    readBooks.push(book);
    displayReadBooks();
    bookIndex = (bookIndex + 1) % books.length;
    showBook();
});

// افزودن کتاب به لیست علاقه‌مندی‌ها
document.getElementById('addToFavorites').addEventListener('click', () => {
    const book = books[bookIndex];

    // بررسی اینکه آیا کتاب قبلاً در لیست علاقه‌مندی‌ها موجود است
    if (favorites.some(b => b.title === book.title)) {
        alert('این کتاب قبلاً به لیست علاقه‌مندی‌ها اضافه شده است.');
        return;
    }

    favorites.push(book);
    displayFavorites();
    bookIndex = (bookIndex + 1) % books.length;
    showBook();
});

// نمایش توضیحات کتاب در مدال
function showBookDescriptionModal(listType, index) {
    let book;

    // انتخاب کتاب از لیست خوانده‌شده یا علاقه‌مندی
    if (listType === 'read') {
        book = readBooks[index];
    } else if (listType === 'favorite') {
        book = favorites[index];
    }

    // تنظیم محتوای مدال
    document.getElementById('bookDescriptionContent').innerHTML = `
    <h5 class='text-primary'>${book.title}</h5>
    <p class='text-success'>نویسنده: ${book.author}</p>
    <p class='description'>${book.description}</p>
    `;

    // نمایش مدال
    const modal = new bootstrap.Modal(document.getElementById('bookModal'));
    modal.show();
}

// نمایش کتاب اولیه
showBook();

// بارگذاری لیست کتاب‌های خوانده‌شده و علاقه‌مندی‌ها
displayReadBooks();
displayFavorites();
